// Theme definitions for Fuel Track AI
export const themes = {
  'soft-blue-white': {
    name: 'ðŸ©µ Soft Blue & White',
    description: 'Calm and clean',
    colors: {
      primary: '#A7C7E7',
      primaryDark: '#7EA6C7',
      primaryLight: '#D0E6F6',
      secondary: '#FFFFFF',
      secondaryLight: '#F5F5F5',
      accent: '#BFD8B8',
      accentDark: '#A3C9A8',
      background: '#FFFFFF',
      surface: '#F9FAFB',
      text: '#333333',
      textSecondary: '#616161'
    }
  },
  'light-lavender-gray': {
    name: 'ðŸ’œ Light Lavender & Gray',
    description: 'Gentle and soothing',
    colors: {
      primary: '#E6E6FA',
      primaryDark: '#C8C8E6',
      primaryLight: '#F8F8FF',
      secondary: '#F8F8FF',
      secondaryLight: '#FFFFFF',
      accent: '#B0C4DE',
      accentDark: '#A9A9B0',
      background: '#F8F8FF',
      surface: '#FFFFFF',
      text: '#444444',
      textSecondary: '#888888'
    }
  },
  'pale-green-cream': {
    name: 'ðŸ’š Pale Green & Cream',
    description: 'Fresh and light',
    colors: {
      primary: '#D4F1E6',
      primaryDark: '#A3C9A8',
      primaryLight: '#E9FFF9',
      secondary: '#FFFDE4',
      secondaryLight: '#FFFFFF',
      accent: '#A3C9A8',
      accentDark: '#BFD8B8',
      background: '#FFFDE4',
      surface: '#FFFFFF',
      text: '#2E2E2E',
      textSecondary: '#616161'
    }
  },
  'powder-blue-peach': {
    name: 'ðŸ©µ Powder Blue & Peach',
    description: 'Soft and appealing',
    colors: {
      primary: '#B0E0E6',
      primaryDark: '#7EC8E3',
      primaryLight: '#E0FFFF',
      secondary: '#FFF0E1',
      secondaryLight: '#FFDAB9',
      accent: '#FFDAB9',
      accentDark: '#FFCBA4',
      background: '#FFF0E1',
      surface: '#FFFFFF',
      text: '#3A3A3A',
      textSecondary: '#616161'
    }
  },
  'misty-rose-gray': {
    name: 'ðŸŒ¸ Misty Rose & Gray',
    description: 'Light and gentle',
    colors: {
      primary: '#FFE4E1',
      primaryDark: '#F5C6CB',
      primaryLight: '#FFF5F5',
      secondary: '#F5F5F5',
      secondaryLight: '#FFFFFF',
      accent: '#C1E1C1',
      accentDark: '#A3C9A8',
      background: '#F5F5F5',
      surface: '#FFFFFF',
      text: '#222222',
      textSecondary: '#616161'
    }
  },
};

export const applyTheme = (themeName) => {
  const theme = themes[themeName];
  if (!theme) return;

  const root = document.documentElement;
  const colors = theme.colors;

  // Apply CSS custom properties
  root.style.setProperty('--primary-color', colors.primary);
  root.style.setProperty('--primary-dark', colors.primaryDark);
  root.style.setProperty('--primary-light', colors.primaryLight);
  root.style.setProperty('--secondary-color', colors.secondary);
  root.style.setProperty('--secondary-light', colors.secondaryLight);
  root.style.setProperty('--accent-color', colors.accent);
  root.style.setProperty('--accent-dark', colors.accentDark);
  root.style.setProperty('--background-color', colors.background);
  root.style.setProperty('--surface-color', colors.surface);
  root.style.setProperty('--text-color', colors.text);
  root.style.setProperty('--text-secondary', colors.textSecondary);

  // Update gradients
  root.style.setProperty('--gradient-primary', `linear-gradient(135deg, ${colors.primary} 0%, ${colors.primaryDark} 100%)`);
  root.style.setProperty('--gradient-secondary', `linear-gradient(135deg, ${colors.secondary} 0%, ${colors.secondaryLight} 100%)`);
  root.style.setProperty('--gradient-accent', `linear-gradient(135deg, ${colors.accent} 0%, ${colors.accentDark} 100%)`);

  // Special handling for dark themes
  if (themeName === 'highway-signals') {
    document.body.classList.add('dark-theme');
  } else {
    document.body.classList.remove('dark-theme');
  }

  // Save theme preference
  localStorage.setItem('selectedTheme', themeName);
};

export const getCurrentTheme = () => {
  return localStorage.getItem('selectedTheme') || 'road-trip';
};
