// frontend/src/components/Profile.js
import React, { useState, useEffect } from 'react';
import { getSummary } from '../services/api';

export default function Profile({ user, onLogout, onUpdateProfile }) {
  const [stats, setStats] = useState({
    totalEntries: 0,
    totalSpent: 0,
    totalLiters: 0,
    avgPrice: 0,
    joinDate: null
  });
  
  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({
    name: user.name,
    email: user.email
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    loadUserStats();
    const joinDate = new Date(user.createdAt || Date.now());
    setStats(prev => ({
      ...prev,
      joinDate: joinDate.toLocaleDateString('en-IN', {
        day: 'numeric',
        month: 'long',
        year: 'numeric'
      })
    }));
  }, [user]);

  const loadUserStats = async () => {
    try {
      const summary = await getSummary();
      if (summary && !summary.error) {
        setStats(prev => ({
          ...prev,
          totalEntries: summary.totalEntries || 0,
          totalSpent: summary.totalCost || 0,
          totalLiters: summary.totalLiters || 0,
          avgPrice: summary.avgPrice || 0
        }));
      }
    } catch (err) {
      console.error('Failed to load user stats:', err);
    }
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      // TODO: Implement profile update API call
      // const response = await updateProfile(editForm);
      
      // Simulate API call for now
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      setSuccess('Profile updated successfully!');
      setIsEditing(false);
      
      // Call parent update function if provided
      if (onUpdateProfile) {
        onUpdateProfile(editForm);
      }
      
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      setError('Failed to update profile. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      onLogout();
    }
  };

  return (
    <div className="row">
      <div className="col-md-4">
        <div className="card">
          <div className="card-body text-center">
            <div className="user-avatar mx-auto mb-3" style={{width: '80px', height: '80px', fontSize: '2rem'}}>
              {user.name.charAt(0).toUpperCase()}
            </div>
            <h4>{user.name}</h4>
            <p className="text-muted">{user.email}</p>
            <div className="mt-3">
              <small className="text-muted">Member since</small>
              <div className="fw-bold">{stats.joinDate}</div>
            </div>
          </div>
        </div>
      </div>
      
      <div className="col-md-8">
        <div className="card">
          <div className="card-header">
            <h5 className="mb-0">üìä Your Fuel Tracking Stats</h5>
          </div>
          <div className="card-body">
            <div className="row">
              <div className="col-6 col-md-3 text-center mb-3">
                <div className="h4 text-primary mb-1">{stats.totalEntries}</div>
                <small className="text-muted">Total Entries</small>
              </div>
              <div className="col-6 col-md-3 text-center mb-3">
                <div className="h4 text-success mb-1">‚Çπ{stats.totalSpent.toFixed(0)}</div>
                <small className="text-muted">Total Spent</small>
              </div>
              <div className="col-6 col-md-3 text-center mb-3">
                <div className="h4 text-info mb-1">{stats.totalLiters.toFixed(1)}L</div>
                <small className="text-muted">Total Fuel</small>
              </div>
              <div className="col-6 col-md-3 text-center mb-3">
                <div className="h4 text-warning mb-1">‚Çπ{stats.avgPrice.toFixed(2)}</div>
                <small className="text-muted">Avg Price/L</small>
              </div>
            </div>
            
            <hr/>
            
            <div className="row">
              <div className="col-md-6">
                <h6>üéØ Quick Actions</h6>
                <div className="d-grid gap-2">
                  <button className="btn btn-outline-primary btn-sm">üìä View Detailed Stats</button>
                  <button className="btn btn-outline-success btn-sm">üì§ Export Data</button>
                </div>
              </div>
              <div className="col-md-6">
                <h6>üèÜ Achievements</h6>
                <div className="d-flex flex-wrap gap-2">
                  <span className="badge bg-primary">üöó First Entry</span>
                  <span className="badge bg-success">üíØ 10+ Entries</span>
                  <span className="badge bg-info">üìÖ Regular User</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
