// frontend/src/components/Chatbot.js
import React, { useState } from 'react';
import { chatbotMessage } from '../services/api';

export default function Chatbot(){
  const [messages, setMessages] = useState([
    { from: 'bot', text: 'Hi! I\'m your AI Fuel Assistant ü§ñ\n\nI can help you with:\n‚Ä¢ Finding nearby gas stations üó∫Ô∏è\n‚Ä¢ Analyzing your fuel expenses üìä\n‚Ä¢ Tracking fuel efficiency üìà\n‚Ä¢ Price comparisons üí∞\n\nWhat would you like to know?' }
  ]);
  const [input, setInput] = useState('');
  const [isMinimized, setIsMinimized] = useState(false);
  const [quickActions] = useState([
    { text: 'Find stations near me', icon: 'üó∫Ô∏è' },
    { text: 'Monthly expenses', icon: 'üìä' },
    { text: 'Average fuel price', icon: 'üí∞' },
    { text: 'Last fill-up details', icon: '‚õΩ' }
  ]);

  async function send(message = null){
    const messageText = message || input;
    if(!messageText.trim()) return;
    
    const userMsg = { from:'user', text: messageText };
    setMessages(m=>[...m, userMsg]);
    
    // Show typing indicator
    setMessages(m=>[...m, { from:'bot', text: 'ü§ñ Typing...', isTyping: true }]);
    
    // try to get location if message asks for 'near' or 'nearby'
    let pos = null;
    if (/(near|nearby|around|close|find.*station|find.*pump)/i.test(messageText)) {
      try {
        pos = await new Promise((resolve, reject) => {
          navigator.geolocation.getCurrentPosition(
            p => resolve(p.coords), 
            err => {
              console.log('Location error:', err);
              resolve(null);
            },
            { timeout: 10000, enableHighAccuracy: true }
          );
        });
      } catch(e){ 
        console.log('Location exception:', e);
        pos = null; 
      }
    }

    try {
      const payload = {
        text: messageText,
        lat: pos?.latitude,
        lng: pos?.longitude
      };

      const res = await chatbotMessage(payload);
      
      // Remove typing indicator
      setMessages(m => m.filter(msg => !msg.isTyping));
      
      const botReply = res.reply || formatStationResponse(res) || "I couldn't understand that. Try asking 'help' to see what I can do!";
      setMessages(m=>[...m, { from:'bot', text: botReply }]);
    } catch (error) {
      // Remove typing indicator
      setMessages(m => m.filter(msg => !msg.isTyping));
      
      setMessages(m=>[...m, { 
        from:'bot', 
        text: '‚ö†Ô∏è Sorry, I encountered an error. Please try again or check your internet connection.' 
      }]);
    }
    
    if (!message) setInput('');
  }

  function handleQuickAction(actionText) {
    send(actionText);
  }

  function clearChat() {
    setMessages([
      { from: 'bot', text: 'Chat cleared! How can I help you today? üòä' }
    ]);
  }

  function formatStationResponse(res){
    // Handle the new station response format
    if (res.hasStations && res.stations) {
      return res.reply; // The backend now formats the response
    }
    
    // Fallback for direct API responses
    if (res.success && res.stations) {
      const stationList = res.stations.slice(0, 5).map((station, index) => 
        `${index + 1}. ${station.name}\n   üìç ${station.address}\n   üìè ${station.distance ? `${station.distance}m away` : ''}`
      ).join('\n\n');
      return `Found ${res.stations.length} stations:\n\n${stationList}`;
    }
    
    // Legacy format support
    if (res.data && res.data.results) {
      return res.data.results.slice(0,5).map(s => `${s.name} ‚Äî ${s.vicinity || s.formatted_address}`).join('\n');
    }
    if (res.data && res.data.elements) {
      return res.data.elements.slice(0,5).map(e => `${e.name || 'Unknown'} ‚Äî ${e.address || ''}`).join('\n');
    }
    
    return res.reply || "I couldn't find any stations nearby.";
  }

  return (
    <div className={`chatbot-container ${isMinimized ? 'minimized' : ''}`}>
      {/* Chatbot Header */}
      <div className="chatbot-header">
        <div className="d-flex align-items-center">
          <div className="chatbot-avatar">
            <span>ü§ñ</span>
          </div>
          <div className="ms-3">
            <h5 className="mb-0 gradient-text">AI Fuel Assistant</h5>
            <small className="text-muted">Online ‚Ä¢ Ready to help</small>
          </div>
        </div>
        <div className="chatbot-controls">
          <button 
            className="btn btn-sm btn-outline-light me-2" 
            onClick={clearChat}
            title="Clear chat"
          >
            üóëÔ∏è
          </button>
          <button 
            className="btn btn-sm btn-outline-light" 
            onClick={() => setIsMinimized(!isMinimized)}
            title={isMinimized ? 'Expand' : 'Minimize'}
          >
            {isMinimized ? '‚¨ÜÔ∏è' : '‚¨áÔ∏è'}
          </button>
        </div>
      </div>

      {/* Chat Messages */}
      {!isMinimized && (
        <>
          <div className="chatbot-messages">
            <div className="d-flex flex-column">
              {messages.map((m,i)=>(
                <div key={i} className={`chat-message ${m.from}`}>
                  {m.from === 'bot' && (
                    <div className="message-avatar">ü§ñ</div>
                  )}
                  <div className="message-content">
                    <pre style={{margin:0,whiteSpace:'pre-wrap', fontFamily:'inherit'}}>{m.text}</pre>
                    <small className="message-time">
                      {new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                    </small>
                  </div>
                  {m.from === 'user' && (
                    <div className="message-avatar user-avatar">üë§</div>
                  )}
                </div>
              ))}
            </div>
          </div>

          {/* Quick Actions */}
          <div className="quick-actions">
            <small className="text-muted mb-2 d-block">Quick Actions:</small>
            <div className="d-flex flex-wrap gap-2">
              {quickActions.map((action, i) => (
                <button
                  key={i}
                  className="btn btn-sm btn-outline-primary quick-action-btn"
                  onClick={() => handleQuickAction(action.text)}
                >
                  {action.icon} {action.text}
                </button>
              ))}
            </div>
          </div>

          {/* Input Area */}
          <div className="chatbot-input">
            <div className="input-group">
              <input 
                className="form-control" 
                value={input} 
                onChange={e=>setInput(e.target.value)}
                onKeyPress={e => e.key === 'Enter' && send()}
                placeholder="Type your message..." 
              />
              <button 
                className="btn btn-primary" 
                onClick={() => send()} 
                disabled={!input.trim()}
              >
                <span className="d-none d-sm-inline">Send</span>
                <span className="d-sm-none">üì§</span>
              </button>
            </div>
            <div className="mt-2">
              <small className="text-muted">
                üí° Try: "find stations", "monthly expenses", "average price", or "help"
              </small>
            </div>
          </div>
        </>
      )}
    </div>
  );
}
