// frontend/src/components/StationSearch.js
import React, { useState } from 'react';
import { findStations } from '../services/api';

export default function StationSearch({ onStationSelect }) {
  const [stations, setStations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function searchNearby() {
    if (!navigator.geolocation) {
      setError('Geolocation is not supported by this browser');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const position = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, {
          timeout: 10000,
          enableHighAccuracy: true
        });
      });

      const { latitude, longitude } = position.coords;
      const data = await findStations(latitude, longitude);
      
      if (data.provider === 'google' && data.data.results) {
        setStations(data.data.results);
      } else if (data.provider === 'osm' && data.data.elements) {
        setStations(data.data.elements.map(e => ({
          name: e.tags?.name || 'Unknown Station',
          vicinity: e.tags?.['addr:street'] || 'Unknown Address',
          place_id: e.id,
          geometry: { location: { lat: e.lat, lng: e.lon } }
        })));
      } else {
        setStations([]);
      }
    } catch (err) {
      if (err.code === 1) {
        setError('Location access denied. Please enable location services.');
      } else if (err.code === 2) {
        setError('Location unavailable. Please try again.');
      } else if (err.code === 3) {
        setError('Location request timed out. Please try again.');
      } else {
        setError('Failed to find stations. Please try again.');
      }
      console.error('Station search error:', err);
    } finally {
      setLoading(false);
    }
  }

  function selectStation(station) {
    if (onStationSelect) {
      onStationSelect({
        name: station.name,
        placeId: station.place_id,
        address: station.vicinity || station.formatted_address
      });
    }
  }

  return (
    <div className="card">
      <div className="card-header">
        <h5>Find Nearby Gas Stations</h5>
      </div>
      <div className="card-body">
        <button 
          className="btn btn-primary mb-3" 
          onClick={searchNearby}
          disabled={loading}
        >
          {loading ? 'Searching...' : 'Find Stations Near Me'}
        </button>

        {error && (
          <div className="alert alert-danger" role="alert">
            {error}
          </div>
        )}

        {stations.length > 0 && (
          <div>
            <h6>Nearby Stations:</h6>
            <div className="list-group">
              {stations.slice(0, 10).map((station, index) => (
                <button
                  key={station.place_id || index}
                  className="list-group-item list-group-item-action"
                  onClick={() => selectStation(station)}
                >
                  <div className="d-flex w-100 justify-content-between">
                    <h6 className="mb-1">{station.name}</h6>
                  </div>
                  <p className="mb-1">{station.vicinity || station.formatted_address}</p>
                </button>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
