// Theme Selector Component
import React, { useState } from 'react';
import { themes, applyTheme } from '../utils/themes';

export default function ThemeSelector({ currentTheme, onThemeChange }) {
  const [isOpen, setIsOpen] = useState(false);

  const handleThemeSelect = (themeName) => {
    applyTheme(themeName);
    onThemeChange(themeName);
    setIsOpen(false);
  };

  return (
    <div className="theme-selector dropdown">
      <button
        className="theme-selector-btn dropdown-toggle"
        onClick={() => setIsOpen(!isOpen)}
        type="button"
        title="Change Theme"
      >
        ðŸŽ¨ Themes
      </button>
      
      {isOpen && (
        <div className="theme-dropdown-menu">
          <div className="theme-dropdown-header">
            <h6>ðŸŽ¨ Choose Your Theme</h6>
            <button 
              className="theme-close-btn"
              onClick={() => setIsOpen(false)}
            >
              âœ•
            </button>
          </div>
          
          <div className="theme-grid">
            {Object.entries(themes).map(([key, theme]) => (
              <div
                key={key}
                className={`theme-option ${currentTheme === key ? 'active' : ''}`}
                onClick={() => handleThemeSelect(key)}
              >
                <div className="theme-preview">
                  <div 
                    className="theme-color-primary"
                    style={{ backgroundColor: theme.colors.primary }}
                  ></div>
                  <div 
                    className="theme-color-secondary"
                    style={{ backgroundColor: theme.colors.secondary }}
                  ></div>
                  <div 
                    className="theme-color-accent"
                    style={{ backgroundColor: theme.colors.accent }}
                  ></div>
                </div>
                <div className="theme-info">
                  <div className="theme-name">{theme.name}</div>
                  <div className="theme-description">{theme.description}</div>
                </div>
                {currentTheme === key && (
                  <div className="theme-active-indicator">âœ“</div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
