      surface: '#F8F9F9',
      text: '#2C3E50',
      textSecondary: '#626567'
    }
  },
  'pale-green-cream': {
    name: 'ðŸ’š Pale Green & Cream',
    description: 'Fresh and light (bolder)',
    colors: {
      primary: '#229954',
      primaryDark: '#145A32',
      primaryLight: '#58D68D',
      secondary: '#F9E79F',
      secondaryLight: '#F7DC6F',
      accent: '#F1C40F',
      accentDark: '#B7950B',
      background: '#FDFEFE',
      surface: '#F8F9F9',
      text: '#212F3C',
      textSecondary: '#626567'
    }
  },
  'powder-blue-peach': {
    name: 'ðŸ©µ Powder Blue & Peach',
    description: 'Soft and appealing (bolder)',
    colors: {
      primary: '#2980B9',
      primaryDark: '#1C2833',
      primaryLight: '#5499C7',
      secondary: '#F5B041',
      secondaryLight: '#F8C471',
      accent: '#CA6F1E',
      accentDark: '#873600',
      background: '#FDFEFE',
      surface: '#F8F9F9',
      text: '#212F3C',
      textSecondary: '#626567'
    }
  },
  'misty-rose-gray': {
    name: 'ðŸŒ¸ Misty Rose & Gray',
    description: 'Light and gentle (bolder)',
    colors: {
      primary: '#C0392B',
      primaryDark: '#922B21',
